package org.creativecodingmap.workflows

import org.creativecodingmap.core.*

fun exampleWorkflow() =
    workflow {
        var artifact = useTool(Processing()) {
            importLibrary("hemesh")
            importLibrary("controlP5")
            runSketch()
            createImage(100, 100)
            drawRectangle(100.0, 100.0, 100.0, 100.0)

            drawLoop {
                clearBackground(1, 2.0, 1)

                useLibrary(library = OrxFx()) {
                    applyGaussianBlur(10, 10)
                }

                for (i in 0 until 3) {
                    useTechnique<ImageArtifact>(PerlinNoise())
                }
                saveImage("bla.jpg")
            }

        }

        var artifact2 = useTool(P5Js()) {
            importLibrary("p5ascii")
            runSketch()
            loadImageArtifact(artifact)
            saveImage("bla2.jpg")
        }

        deliver(artifact)
        deliver(artifact2)
    }
